package 복습;

import javax.tools.DocumentationTool.Location;

public class 기본형심화2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//기본형 4가지 정수(int), 실수(실수), 문자(char), 논리(boolean
		
		//오늘의 나의 아침 정보
		double temp = 1.2;
		int day = 7;
		char color = 'w';
		boolean breakfast = true;
		
		String location = "선릉역";//기본형은 아니지만 자주쓰임 쓸 땐 " "
		
		System.out.println("오늘의 날씨는" + temp + "도"); // 
		System.out.println("오늘은 " + day + "일 입니다."); // 
		System.out.println("오늘 내가입은 옷 색은 " + color + "색"); // 
		System.out.println("오늘 아침을 먹었나요?" + breakfast); // 
		System.out.println("나의 위치는?" + location); //
				
		

	}

}
